// Function to get knowledge base as text
function getKnowledgeBaseText() {
    let text = "=== SUMMARY ===\n" + KNOWLEDGE_BASE.summary + "\n\n";
    
    text += "=== Q&A PAIRS (Exact Answers) ===\n";
    KNOWLEDGE_BASE.qaPairs.forEach(qa => {
        text += `Q: ${qa.question}\nA: ${qa.answer}\n\n`;
    });
    
    text += "=== DETAILED INFORMATION ===\n";
    KNOWLEDGE_BASE.entries.forEach(entry => {
        text += `[${entry.category}] ${entry.text}\n\n`;
    });
    
    return text;
}

// Export for use in index.html
if (typeof window !== 'undefined') {
    window.KNOWLEDGE_BASE = KNOWLEDGE_BASE;
    window.getKnowledgeBaseText = getKnowledgeBaseText;
}
